package com.sist.myapp;
import java.util.*;

import org.springframework.context.ApplicationContext;
import org.springframework.context.support.ClassPathXmlApplicationContext;

import com.sist.dao.*;

import lombok.Setter;
public class MainClass {
	@Setter
	private SeoulDAO dao;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] table= {"","seoul_location","seoul_nature","seoul_shop"};
		Scanner scan=new Scanner(System.in);
		System.out.println("테이블 선택: ");
		int no=scan.nextInt();
		Map map=new HashMap();
		map.put("seoul_tbl", table[no]);
		ApplicationContext app=new ClassPathXmlApplicationContext("app.xml");
		MainClass mc=(MainClass)app.getBean("mc");
		// 싱글턴 메모리 1개 사용, 모든 클래스에서 재사용이 가능, 다른 클래스에 영향력이 없다 (스프링 => 독립적인 클래스)
		// 결합성이 낮은 프로그램 (유지보수가 편리하게, 분업일 경우에..), 형식이 동일(구조)
		// 객체지향의 객체에 대해 설명
		List<SeoulVO> list=mc.dao.seoulListData(map);
		for(SeoulVO vo:list)
		{
			System.out.println("명소: "+vo.getTitle());
			System.out.println("주소: "+vo.getAddress());
			System.out.println("설명: "+vo.getMsg());
			System.out.println("=====================================");
		}
		System.out.println("상세하게 볼 번호 입력");
		int i=scan.nextInt();
		map.put("seoul_tbl", table[no]);
		map.put("no", i);
		SeoulVO vo=mc.dao.seoulDetailData(map);
		System.out.println("번호:"+vo.getNo());
		System.out.println("제목:"+vo.getTitle());
		System.out.println("주소:"+vo.getAddress());
		System.out.println("설명:"+vo.getMsg());
		
	}

}
